let TempData=datatable(
    UserName: string,
    TrainingStatus: string,
    AccessLevel: string
) [
    "Manish Kumar", "Completed", "Admin",
    "Kannu", "Not Completed", "User",
    "Shinda", "Completed", "Security Admin",
    "Arora", "Not Completed", "Guest"
];
TempData
| extend TrainingStatus = case(
    TrainingStatus == "Completed", strcat("🟩 ", TrainingStatus),
    strcat("🟥 ", TrainingStatus)
)

let TempData=datatable(
    DevicName: string,
    DeviceType: string,
    Status: string
) [
    "LinuxVM", "Server","Online",
    "WindowsVM", "Server","Online",
    "HippaFirewall", "NetworkDevice","Online"
];
TempData

// Azure SQL Logins
let SQLLogins = AzureDiagnostics
| where Category == "SQLSecurityAuditEvents"
| where action_name_s in ("DATABASE AUTHENTICATION FAILED", "DATABASE AUTHENTICATION SUCCEEDED")
| summarize Count = count() by Status = iff(action_name_s == "DATABASE AUTHENTICATION FAILED", "Failed", "Success");
// Windows VM Logins
let WinVMLogins = SecurityEvent
| where EventID in (4624, 4625) // 4624=Success, 4625=Failed
| summarize Count = count() by Status = iff(EventID == 4625, "Failed", "Success");
// Linux VM Logins
let LinuxVMLogins = Syslog
| where Facility in ("authpriv","auth")
| where ProcessName == "sshd"
| where SyslogMessage has_any ("Failed password", "Accepted password")
| summarize Count = count() by Status = iff(SyslogMessage has "Failed password", "Failed", "Success");
// Azure AD Logins
let AzureADLogins = SigninLogs
| where isnotempty(UserPrincipalName)
| summarize Count = count() by Status = iff(ResultSignature == "FAILURE", "Failed", "Success");
// Combine all and summarize into two rows: Failed & Success
union SQLLogins, WinVMLogins, LinuxVMLogins, AzureADLogins
| summarize TotalCount = sum(Count) by Status
| order by Status asc
